import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Post from '../Post';
import './ProfilePage.css';

export default function ProfilePage() {
  const [profile, setProfile] = useState(null);
  const [sortBy, setSortBy] = useState('latest');
  const { userId } = useParams();

  useEffect(() => {
    fetchProfile();
  }, [userId, sortBy]);

  const fetchProfile = async () => {
    try {
      const response = await fetch(`http://localhost:4000/profile/${userId}?sort=${sortBy}`, {
        credentials: 'include',
      });
      const data = await response.json();
      if (response.ok) {
        setProfile(data);
      } else {
        console.error(data.error);
      }
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  const handleSortChange = (event) => {
    setSortBy(event.target.value);
  };

  if (!profile) return <div>Loading...</div>;

  return (
    <div className="profile-page">
      <div className="posts-section">
        <h2>Posts by {profile.user.username}</h2>
        <div className="sort-container">
          <label htmlFor="sort-select">Sort posts by: </label>
          <select id="sort-select" value={sortBy} onChange={handleSortChange}>
            <option value="latest">Latest</option>
            <option value="votes">Most Votes</option>
          </select>
        </div>
        <div className="user-posts">
          {profile.posts.length > 0 ? (
            profile.posts.map(post => (
              <Post key={post._id} {...post} />
            ))
          ) : (
            <p>No posts yet.</p>
          )}
        </div>
      </div>
      <div className="profile-section">
        <h1>{profile.user.username}'s Profile</h1>
        <div className="user-info">
          <p><strong>Username:</strong> {profile.user.username}</p>
          <p><strong>Joined:</strong> {new Date(profile.user.createdAt).toLocaleDateString()}</p>
          {/* Add more user info here if available */}
        </div>
      </div>
    </div>
  );
}
